css:
/* -------------------------------------------------------------------------------- */
/* Polices */
/* -------------------------------------------------------------------------------- */

@font-face {
    font-family: 'DINPro';
    font-style: normal;
    font-weight: 300;
    src: local('DINPro'), url('https://fonts.cdnfonts.com/s/18774/DINPro-Light tr.woff') format('woff');
}
@font-face {
    font-family: 'DINPro';
    font-style: normal;
    font-weight: 500;
    src: local('DINPro'), url('https://fonts.cdnfonts.com/s/18774/DINPro-Medium tr.woff') format('woff');
}
@font-face {
    font-family: 'DINPro';
    font-style: normal;
    font-weight: 700;
    src: local('DINPro'), url('https://fonts.cdnfonts.com/s/18774/DINPro-Bold tr.woff') format('woff');
}
@font-face {
    font-family: 'DINPro';
    font-style: normal;
    font-weight: 900;
    src: local('DINPro'), url('https://fonts.cdnfonts.com/s/18774/DINPro-Black tr.woff') format('woff');
}

card { 
  font-size: 80%;
  font-family: 'Bebas Neue', sans-serif;
}


/* -------------------------------------------------------------------------------- */
/* Couleurs */
/* -------------------------------------------------------------------------------- */

card.argumentPertinent {
  --color-background: #811da3;
  --color-background-title:#380050;
  --color-text-1: #601362;
  --color-text-2: #8f5991;
  --color-text-3: #a173a2;
}

card.présentationIntègre {
  --color-background: #ff66eb;
  --color-background-title:#990085;
  --color-text-1: #b3009b;
  --color-text-2: #cc00b1;
  --color-text-3: #e566d4;

}

card.exactitudeMathématique {
  --color-background: #08af93;
  --color-background-title:#14555b;
  --color-text-1: #14555b;
  --color-text-2: #5a888c;
  --color-text-3: #749a9e;
}

card.raisonnementValide {
  --color-background: #8dc801;
  --color-background-title:#2f5000;
  --color-text-1: #476205;
  --color-text-2: #7e9150;
  --color-text-3: #92a26b;
}

card.langageRigoureux {
  --color-background: #0054a4;
  --color-background-title:#001350;
  --color-text-1: #0c2861;
  --color-text-2: #546890;
  --color-text-3: #6f80a1;
}

card.honnêtetéIntellectuelle {
  --color-background: #ffc307ff;
  --color-background-title:#524100;
  --color-text-1: #9e7800ff;
  --color-text-2: #c49500ff;
  --color-text-3: #d6b755ff;
}

card.débatRespectueux {
  --color-background: #dc0f0a;
  --color-background-title:#3c0b0b;
  --color-text-1: #960a07;
  --color-text-2: #b55351;
  --color-text-3: #c16e6c;
}



card .famille { color: var(--color-text-1); }

card .sous_famille { color: var(--color-text-2); }

card .Soussousfamille { color: var(--color-text-3); }

/* -------------------------------------------------------------------------------- */
/* Structure */
/* -------------------------------------------------------------------------------- */

bleed {
}

cut {
}

safe {
  background-color: #fff;
  position: relative;
  /* Different Safe Padding */
  padding: 1.2em 2.5em 1.5em 2.5em;
}

.cardName {
  display: none;
}

/* -------------------------------------------------------------------------------- */
/* Entête */
/* -------------------------------------------------------------------------------- */

.header {
  display:flex;
  justify-content:center;
}

.famille {
  text-transform: uppercase;
  font-size:1.8em;
  text-align:center;
  line-height:0.9;
  letter-spacing:0.05em;
  color:var(--color-text-1);
}

.sous_famille {
	font-size:0.6em;
  	letter-spacing:0.05em;
}

.soussousfamille {
	font-size:0.98em;
}



/* -------------------------------------------------------------------------------- */
/* Corps */
/* -------------------------------------------------------------------------------- */

.body {
  display: flex;
  padding-top: 2%;
  flex-flow:column nowrap;
  height: 100%;
  justify-content:space-between;
}

.supersetWrapper .famille {
  max-height:61%;
  width:100%;
  display:block;
  padding:0;
  margin-bottom:1.3%
}

.supersetWrapper .sous_famille {
  width:100%;
  display:block;
  padding:0;
  margin-bottom:2%;
  text-align:center;
}
.contentWrapper{
  display: flex;
  flex-direction: column;
  height:93%;
}


.title {
  	width: 100%;
    text-align: center;
    font-size: 1.1em;
    font-family: 'DINPro',sans-serif;
    line-height: 1em;
    font-weight: 900;
    text-transform: uppercase;
    letter-spacing: 0.05em;
    background-color: var(--color-background-title);
    color: #fff;
    min-height: 2em;
    box-sizing: border-box;
    flex-shrink: 0;
    padding: 3.6% 2% 4% 2%;
    word-wrap: break-word !important;
    display: flex;
    justify-content: center;
    min-height: 2.5em;
    align-items: center;
}
.title > div {
	flex-shrink:1;
  	width:100%;
}

.imageSection {
    min-height: 0;
    flex-shrink: 1;
    display: flex;
    flex-flow: column;
    justify-content: flex-start;
    align-items: flex-start;
    width: 100%;
    height: 100%;
}

.image {
	width: 100%;
    flex-grow: 1;
    /* display: flex; */
    /* justify-content: flex-start; */
    /* align-items: flex-start; */
    text-align: center;
    background-color: var(--color-background);
    padding: 6% 8%;
    min-height: 0;
}

.image img {
    max-width: 100%;
    max-height: 100%;
    position: relative;
    top: 50%;
    transform: translateY(-50%);
}

.texte {
  font-family:'DINpro',sans-serif;
  display: flex;
  flex-flow: column wrap;
  justify-content: space-between;
}

.desc_fr {
  display: flex;
  align-items: center;
  text-transform: uppercase;
  font-weight: 900;
  font-size:0.7em;
  line-height: 1.2em;
  min-height: 3em;
  margin:4% 0 3% 0;
  color: var(--color-text-1);
}

.exemple_fr {
  font-size: 0.70em;
  font-weight: 700;
  line-height: 1.1em;
  color: var(--color-text-2);
}

/* -------------------------------------------------------------------------------- */
/* Pied de page */
/* -------------------------------------------------------------------------------- */

.footer { 
  height: 2%;
  position: absolute;
  bottom: 0;
  right: 0;
  width:2.5em;
  height:2.5em;
  background-color:var(--color-background);
  border-radius:0 0 12px 0;
  display:flex;
  align-items:center;
  justify-content:center;
}

.niveau {
  font-size: 0.7em;
  font-weight: bold;
  text-align: right;
  color: #fff;
}
