css:
/* -------------------------------------------------------------------------------- */
/* Polices */
/* -------------------------------------------------------------------------------- */

@font-face {
    font-family: 'DINPro';
    font-style: normal;
    font-weight: 300;
    src: local('DINPro'), url('https://fonts.cdnfonts.com/s/18774/DINPro-Light tr.woff') format('woff');
}

@font-face {
    font-family: 'DINPro';
    font-style: normal;
    font-weight: 500;
    src: local('DINPro'), url('https://fonts.cdnfonts.com/s/18774/DINPro-Medium tr.woff') format('woff');
}

@font-face {
    font-family: 'DINPro';
    font-style: normal;
    font-weight: 700;
    src: local('DINPro'), url('https://fonts.cdnfonts.com/s/18774/DINPro-Bold tr.woff') format('woff');
}

@font-face {
    font-family: 'DINPro';
    font-style: normal;
    font-weight: 900;
    src: local('DINPro'), url('https://fonts.cdnfonts.com/s/18774/DINPro-Black tr.woff') format('woff');
}



card {
    font-size: 80%;
    font-family: 'Bebas Neue', sans-serif;
}


    /* -------------------------------------------------------------------------------- */
    /* Couleurs */
    /* -------------------------------------------------------------------------------- */

    card.insuffisance {
        --color-background: #811da3;
        --color-text-1: #601362;
        --color-text-2: #8f5991;
        --color-text-3: #a173a2;
    }

    card.influence {
        --color-background: #ff66eb;
        --color-text-1: #b3009b;
        --color-text-2: #cc00b1;
        --color-text-3: #e566d4;
    }

    card.erreurMathématique {
        --color-background: #08af93;
        --color-text-1: #14555b;
        --color-text-2: #5a888c;
        --color-text-3: #749a9e;
    }

    card.paralogisme {
        --color-background: #8dc801;
        --color-text-1: #476205;
        --color-text-2: #7e9150;
        --color-text-3: #92a26b;
    }

    card.détournementDeLaLangue {
        --color-background: #0054a4;
        --color-text-1: #0c2861;
        --color-text-2: #546890;
        --color-text-3: #6f80a1;
    }

    card.tricherie {
        --color-background: #ffc307ff;
        --color-text-1: #9e7800ff;
        --color-text-2: #c49500ff;
        --color-text-3: #d6b755ff;
    }

    card.obstruction {
        --color-background: #dc0f0a;
        --color-text-1: #960a07;
        --color-text-2: #b55351;
        --color-text-3: #c16e6c;
    }



    card .famille {
        color: var(--color-text-1);
    }

    card .sous_famille {
        color: var(--color-text-2);
    }

    card .Soussousfamille {
        color: var(--color-text-3);
    }

/* -------------------------------------------------------------------------------- */
/* Structure */
/* -------------------------------------------------------------------------------- */

bleed {
}

cut {
}

safe {
    background-color: #fff;
    position: relative;
    /* Different Safe Padding */
    /*padding: 1.2em 2.5em 1.5em 2.5em;*/
}

.cardName {
    display: none;
}

/* -------------------------------------------------------------------------------- */
/* Entête */
/* -------------------------------------------------------------------------------- */

.header {
   /* display: flex;*/
    justify-content: center;
    padding-top: 1%;
}

.famille {
    text-transform: uppercase;
    font-size: 2.8em;
    text-align: center;
    line-height: 0.9em;
    letter-spacing: 0.07em;
    color: var(--color-text-1);
}

.sous_famille {
    font-size: 1.7em;
    letter-spacing: 0.05em;
}

.Soussousfamille {
    font-size: 0.9em !important;
}




/* -------------------------------------------------------------------------------- */
/* Corps */
/* -------------------------------------------------------------------------------- */

.body {
    display: flex;
    padding-top: 2%;
    flex-flow: column nowrap;
    height: 100%;
    justify-content: space-between;
    border: 1.4em solid;
    border-color: var(--color-background);
    border-radius: 1em;
}

.supersetWrapper .famille {
    max-height: 61%;
    width: 100%;
    display: block;
    padding: 0;
    margin-bottom: 1.3%
}

.supersetWrapper .sous_famille {
    width: 100%;
    display: block;
    padding: 0;
    margin-bottom: 1%;
    text-align: center;
}

.contentWrapper {
    display: flex;
    flex-direction: column;
    height: 93%;
}


.title {
    width: 100%;
    text-align: center;
    font-size: 3.5em;
    line-height: 1em;
    font-weight: 500;
    text-transform: uppercase;
    letter-spacing: 0.05em;
    color: var(--color-text-1);
    box-sizing: border-box;
    flex-shrink: 0; 
    padding: 2% 5% 1% 5%;
    word-wrap: break-word !important;
    display: flex;
    justify-content: center;
    min-height: 1.4em;
    align-items: center;
}

    .title > div {
        flex-shrink: 1;
        width: 100%;
        overflow-wrap: normal
    }

.imageSection {
    min-height: 0;
    flex-shrink: 1;
    display: flex;
    flex-flow: column;
    justify-content: flex-start;
    align-items: flex-start;
    width: 100%;
    height: 100%;
}

.image {
    width: 100%;
    flex-grow: 1;
    /* display: flex; */
    /* justify-content: flex-start; */
    /* align-items: flex-start; */
    text-align: center;
    /*background-color: var(--color-background-img);*/
    padding: 1% 2% 0 2%;
    min-height: 0;
}

    .image img {
        max-width: 100%;
        max-height: 100%;
        position: relative;
        top: 50%;
        transform: translateY(-50%);
    }

.texte {
    /*display: flex;
    flex-flow: column wrap;*/
    justify-content: space-between;
    background-color: var(--color-background);
    margin: 0% -1% -1% -1%;
}

.desc_fr {
    display: flex;
    align-items: center;
    text-transform: uppercase;
    font-family: 'DINpro',sans-serif;
    font-weight: 900;
    font-size: 1.5em;
    line-height: 1.4em;
    min-height: 3em;
    /*margin:4% 5% 3% 5%;*/

    padding: 2% 5% 2% 5%;
    color: #fff;
}

.exemple_fr {
    font-family: 'DINpro',sans-serif;
    color: var(--color-text-2);
    font-size: 1.45em;
  	font-weight: 600;
    line-height: 1.2em;
    padding: 2% 5% 2% 5%;
    background-color: white;
    -webkit-font-variant-ligatures: no-common-ligatures;
    font-variant-ligatures: no-common-ligatures;
}

/* -------------------------------------------------------------------------------- */
/* Pied de page */
/* -------------------------------------------------------------------------------- */

.footer {
    height: 2%;
    position: absolute;
    bottom: 0;
    right: 0;
    width: 2em;
    height: 2em;
    background-color: #fff;
    border: 1px solid;
    border-color: var(--color-background);
    border-radius: 16px 16px 16px 16px;
    display: flex;
    align-items: center;
    justify-content: center;
}

.niveau {
    font-size: 1.4em;
    font-weight: bold;
    text-align: right;
    color: var(--color-background);
}
