css:
@import url('https://fonts.cdnfonts.com/css/dinpro');

card { 
  font-size: 50%;
  font-family: 'DINPro', sans-serif;
}

.cardpenBleed {
	border:0 !important;
}

.cardContainer {
  width:100%;
  height:100%;
  position:relative;
  background:url('http://assets-argumentum.ourlove.id/Fallacy-back-bg.jpg') repeat;
  background-size:400px;
  border-radius:0.125in;
  overflow:hidden;
}

.cardContent {
  position:absolute;
  top:0;
  z-index:2;
  width:100%;
  height:100%;
  display:flex;
  flex-flow:column wrap;
  justify-content:space-between;
}

/* Vibrant red vignette effect*/
.vignetteOverlay {
  width:200%;
  height:100%;
  position:relative;
  left:-25%;
	-webkit-box-shadow:inset 3px 7px 70px 30px rgba(180, 0, 3,0.7);
    -moz-box-shadow: inset 3px 7px 70px 30px rgba(180, 0, 3,0.7);
    box-shadow: inset 3px 0px 40px 30px rgba(180, 0, 3,0.6);
}

/* Dark vignette effect*/
.vignetteOverlay2 {
  width:150%;
  height:100%;
  position:absolute;
  top:0;
  left:-25%;
  -webkit-box-shadow: inset 3px 7px 63px 4px #000);
  -moz-box-shadow: inset 3px 7px 63px 4px #000;
  box-shadow: inset 3px 0px 40px 0px rgba(0,0,0,0.8);
}

.title {
  display:flex;
  flex-flow:column;
  word-wrap:break-word;
  position:relative;
  width:90%;
  height:54%;
  max-height:3.2em;
  padding:9% 12% 0% 9%;
  font-weight:900;
  font-size:8em;
  line-height:0.85;
  fill:#fff3de;
  letter-spacing:0.03em;
  align-items:flex-start;
  z-index:3;
}

.title svg {
	margin-bottom:4%;
  	height:29%
}
.title svg text {
	letter-spacing:0.09em;
  	font-size:100px;
    color:red;
    stroke:#720000;
    stroke-width:2px;
    stroke-linecap:butt;
    stroke-linejoin:miter;
    stroke-opacity:0.3;
}

.pieces {
  	height:54%;
  	position:absolute;
  	z-index:1;
  	top:36%;
  	right:0;
  	margin-right:3%;
}

.pieces img {
  height:95%;
}

.footer {
	color:#fff;
  	text-transform:uppercase;
  	width:100%;
  	text-align:center;
  	font-family:'Bebas Neue';
  	letter-spacing:0.1em;
  	font-size:2.2em;
  	padding-bottom:3%;
  	fill:#fff;
}
