css:
@import url('https://fonts.cdnfonts.com/css/dinpro');

card { 
  font-size: 100%;
  font-family: 'DINPro', sans-serif;
}

.cardContainer {
  width:100%;
  height:100%;
  padding:5% 7% 5% 5%;
  display:flex;
  flex-flow:column;
}

.titleContainer {
  	width:100%;
  	padding:4%;
  	text-align:center;
  	display:flex;
  	justify-content:center;
}

.title {
  font-family:'Bebas Neue', sans-setif;
  font-size:4.5em;
  padding-right:5%;
  padding-left:0.3%;
  line-height:1.1;
  max-height:1em;
}

.title > div {
  	display:inline-block;
  	margin-right:-0.09em;
}


.title > div:nth-child(1){
	color:#8605ab;
}

.title > div:nth-child(2){
	color:#022cb2;
}

.title > div:nth-child(3){
	color:#01a987;
}

.title > div:nth-child(4){
	color:#bc0546;
}

.familyContainer {
	font-family:'Gadugi', sans-serif;
  	display:flex;
  	width:100%;
  	justify-content:center;
  	margin-bottom:5%;
}

.colorBox {
  	width:2em;
  	height:2em;
  	position:absolute;
  	background-color:purple;
  	flex-shrink:0;
  	left:1em;
  	margin-top:0.1em;
}

.familyText {
  width:100%;
  flex-grow:1;
  margin-left:5%;
}
.familyName {
  	font-size:0.6em;
	text-transform:uppercase;
  	font-weight:900;
  	padding:0.3em 0.3em 0.3em 20%;
  	display:flex;
  	align-items:center;
  	min-height:1.8em;
  	margin-top:-1.6%;
}

/* Insuffisacne */
.familyContainer:nth-child(2) .colorBox{
	background-color:#8605ab;
}

.familyContainer:nth-child(2) .divider {
	background-color:#8605ab;
}

.familyContainer:nth-child(2) .familyText{
	color:#8605ab;
}

.familyContainer:nth-child(2) .familyName{
	background-color:#e1c0ea;
}
/* Detournement de la langue */
.familyContainer:nth-child(3) .colorBox{
	background-color:#daaf02;
}

.familyContainer:nth-child(3) .divider {
	background-color:#daaf02;
}

.familyContainer:nth-child(3) .familyText{
	color:#daaf02;
}

.familyContainer:nth-child(3) .familyName{
	background-color:#f6ebc0;
}
/* Erreur Mathematique */
.familyContainer:nth-child(4) .colorBox{
	background-color:#6ab203;
}

.familyContainer:nth-child(4) .divider {
	background-color:#6ab203;
}

.familyContainer:nth-child(4) .familyText{
	color:#6ab203;
}
.familyContainer:nth-child(4) .familyName{
	background-color:#daecc0;
}
/* Erreur Paralogisme */
.familyContainer:nth-child(5) .colorBox{
	background-color:#01a987;
}

.familyContainer:nth-child(5) .divider {
	background-color:#01a987;
}

.familyContainer:nth-child(5) .familyText{
	color:#01a987;
}
.familyContainer:nth-child(5) .familyName{
	background-color:#bfe9e1;
}
/* Influnence */
.familyContainer:nth-child(6) .colorBox{
	background-color:#022cb2;
}

.familyContainer:nth-child(6) .divider {
	background-color:#022cb2;
}

.familyContainer:nth-child(6) .familyText{
	color:#022cb2;
}
.familyContainer:nth-child(6) .familyName{
	background-color:#c0caec;
}
/* Thricerie */
.familyContainer:nth-child(7) .colorBox{
	background-color:#bc0546;
}

.familyContainer:nth-child(7) .divider {
	background-color:#bc0546;
}

.familyContainer:nth-child(7) .familyText{
	color:#bc0546;
}
.familyContainer:nth-child(7) .familyName{
	background-color:#eec0d1;
}

/* Obstruction */
.familyContainer:nth-child(8) .colorBox{
	background-color:#af1313;
}

.familyContainer:nth-child(8) .divider {
	background-color:#af1313;
}

.familyContainer:nth-child(8) .familyText{
	color:#af1313;
}

.familyContainer:nth-child(8) .familyName{
	background-color:#ebc4c4;
}

.familyDesc {
  	width:100%;
  	font-size:0.4em;
  	padding-left:20%;
  	min-height:2em;
  	display:flex;
  	margin-bottom:0.3em;
  	align-items:center;
  	color:#000;
}

.divider {
		height:0.1em;
  		background-color:black;
}
