css:
@import url('https://fonts.cdnfonts.com/css/dinpro');

safe {
	overflow:hidden;
}

card {
	background-color:#fff;
  	font-size:100%;
  	margin:5px;
  	border-radius:30px;
}

.cardContainer {
  	width:100%;
    height:100%;
  	padding:7%;
}  

.cardContent {
	width:100%;
  	height:100%;
 	border:1.5px solid #fff;
  	border-radius:0.125in;
  	font-family:'DINPro';
  	padding:10% 4% 12% 4%;
    display:flex;
    flex-flow:column;
    justify-content:space-between;
  	overflow:hidden;
 	word-spacing: 00px; 
  	align-items:center;
}

.image {
  flex-grow:1;
  display:flex;
  align-items:flex-end;
  justify-content:center;
  min-height:0;
  margin-top:1em;
}

.image img {
  max-width:100%;
  max-height:100%;
}

.title {
	font-weight:900;
 	color:#fff;
 	text-align:center;
  	font-size:1.2em;
  	text-transform:uppercase;
  	display:flex;
  	justify-content:center;
}

.title > div {
	width:5px;
  	display:table;
    white-space: pre-wrap; /* css-3 */
    white-space: -moz-pre-wrap; /* Mozilla, since 1999 */
    white-space: -pre-wrap; /* Opera 4-6 */
    white-space: -o-pre-wrap; /* Opera 7 */
    word-wrap: break-word; /* Internet Explorer 5.5+ */
}
