/* -------------------------------------------------------------------------------- */
/* Corps */
/* -------------------------------------------------------------------------------- */

.body {
  padding: 9%;
  height: 100%;
  border: 1.8px solid #000;
  border-radius: 0.125in;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
}

.title {
  font-size: 1.2em;
  font-weight: 900;
  letter-spacing:0.08em;
  font-family: 'DINPro';
  line-height: 1em;
  text-align: center;
  min-height:15.7%;
  text-transform: uppercase;
  display:flex;
  justify-content:center;
  align-items:center;
  padding: 3%;
  color:#fff;
  border-radius:6px 6px 0 0;
  background-color:#af1313;

}

.texte {
  display: flex;
  flex-direction: column;
  justify-content: flex-end;
  font-family:'DINPro';
  font-size:0.8em;
  text-transform:uppercase;
  font-weight:bold;
}

.enjeu {
  font-size: 0.8em;
  line-height: 1.3;
  padding-bottom: 0.5em;
}

.roles {
  display: flex;
  justify-content: space-between;
  font-size: 0.8em;
  line-height:1;
  border-top:2px solid #af1313;
  border-bottom:2px solid #af1313;
  padding: 1em 0;
}

.roles img {
  margin-right:0.3em;
}

.roles span {
	flex-grow:0;
}

.roles .piocheur img {
	width:2em;
  	height:2em;
}

.roles .baratineur img {
	width:1.7em;
  	height:1.7em;
}


.roles > div {
	display:flex;
	align-items:center;
  	flex-basis:50%;
}

.piocheur {
    background-repeat: no-repeat;
    background-size: 10%;
  justify-content:center;
}

.baratineur {
   background-repeat: no-repeat;
   background-size: 10%;
   margin-left:5%;
   justify-content:center;

}

.contexte {
  font-size: 0.8em;
  line-height: 1.1;
  padding-bottom: 0.5em;
  font-weight:bold;
}


.suggestion {
  font-size: 0.7em;
  line-height: 1.1em;
  font-family:'DINPro';
  display:flex;
  margin-left:-8%;
  align-items:flex-start;
}

.suggestion img {
	width:10%;
  margin-right:3%;
}
/* -------------------------------------------------------------------------------- */
/* Pied de page */
/* -------------------------------------------------------------------------------- */

.footer { 
	position:absolute;
  	padding:0.3em;
   	bottom:-2%;
  	left:50%;
  	transform:translateX(-50%);
  	background-color:#fff;
 	display:flex;
  	align-items:center;
}

.footer img {
  max-width:0.5em;
  max-height:0.5em;
  margin:0.2em;
}
.footer img:first-child {
  max-width:0.7em;
  max-height:0.7em;
}

.footer span {
	font-size:0.6em;
  	background-color:#fff;
}
