/* -------------------------------------------------------------------------------- */
/* Corps */
/* -------------------------------------------------------------------------------- */

.body {
  padding-top: 2%;
  display: flex;
  flex-direction: column;
  height: 88%;
  justify-content: space-between;
}

.title {
  font-size: 1.1em;
  font-weight: 900;
  font-family: 'Raleway';
  line-height: 1.3em;
  min-height: 8%;
  order: 1;
  text-align: center;
  text-transform: uppercase;

}

.roles {
  display: flex;
  flex-direction: column;
  justify-content: flex-end;
  order: 3;
}

.texte {
  display: flex;
  flex-direction: column;
  justify-content: flex-end;
  order: 3;
}

.contexte {
  font-size: 0.8em;
  line-height: 1.2em;
  font-weight: bold;
  padding-bottom: 0.5em;
}

.enjeu {
  font-size: 0.8em;
  line-height: 1.2em;
  font-weight: bold;
  padding-bottom: 0.5em;
}

.suggestion {
  font-size: 0.7em;
  line-height: 1.1em;
}

/* -------------------------------------------------------------------------------- */
/* Pied de page */
/* -------------------------------------------------------------------------------- */

.footer { 
  height: 2%;
  position: absolute;
  bottom: 0.3em;
  right: 0.3em;
}

.niveau {
  font-size: 0.7em;
  font-weight: bold;
  text-align: right;
  color: grey;
}
