Policies.en-us:
// *************************
// ALL-SRC
// this will be added to default and all other src which are set
all-src: 'self'
all-src: https:
all-src: *
all-src: 'unsafe-inline'

// *************************
// SCRIPT-SRC

// This is for the default asp.net web forms script block in Dnn 7 - 9
// For now it's disabled, as it would deactivate 'unsafe-inline'
// script-src: 'sha256-1lxkJKcAqLep9MQngjrTJpfkCA56HejJ0oNvP3hq0gI='

// This will allow just about any script to run
script-src: 'unsafe-eval'
script-src: 'unsafe-hashes'

// *************************
